import React, { useState, useEffect, useRef  } from "react";
import { Button, Col, Container, Row } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom"
// import LoginComponent from "../loginComponent/LoginComponent";
import "./WelcomeStyles.css"
import CategoryTab from "./CategoryTag";
import axios from "axios";
// import { apiClient } from "../api/ApiClient";

function WelcomeComponent() {
  const navigate = useNavigate();
  const containerRef = useRef(null);
  const [scroll, setScroll] = useState(0)
  const [pageWidth, setPageWidth] = useState(window.innerWidth);
  const [left, setLeft] = useState(true)
  const [right, setRight] = useState(true)
  const [scrolled, setScrolled] = useState(0)
  const [suggestedHotels, setSuggestedHotels] = useState([])
  const [search, setSearch] = useState('')
  const [hotelResult, setHotelResult] = useState([])
  const [regionResult, setRegionResult] = useState('')
  const [clicked, setClicked] = useState(false)

  useEffect(() => {
    const handleResize = () => {
      setPageWidth(window.innerWidth);
    };

    window.addEventListener('resize', handleResize);

    // Clean up the event listener
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  useEffect(()=> {
    if (containerRef.current){
      if (scroll > containerRef.current.scrollWidth - containerRef.current.clientWidth)
        setScroll(containerRef.current.scrollWidth - containerRef.current.clientWidth)
      }
  }, [pageWidth])

  useEffect(()=> {
    console.log(scrolled)
    if (containerRef.current){

      // if page width is smaller than the scroll left then set it back

      if (scroll >= containerRef.current.scrollWidth - containerRef.current.clientWidth){
        setScroll(containerRef.current.scrollWidth - containerRef.current.clientWidth)
        containerRef.current.scrollLeft = containerRef.current.scrollWidth - containerRef.current.clientWidth
        setRight(false)
      }
      else {
        containerRef.current.scrollLeft = scroll;
        setRight(true)
      }

      if (scroll <= 0){
        setScroll(0)
        containerRef.current.scrollLeft = 0
        setLeft(false)
      }
      else {
        containerRef.current.scrollLeft = scroll;
        setLeft(true)
      }
    }
    console.log("Scroll Left: ", containerRef.current?.scrollLeft)
  }, [scrolled, pageWidth])


  const container = containerRef.current;
  console.log(container)
  useEffect(() => {
    if (containerRef.current) {
      containerRef.current.scrollLeft = 0;
      setScroll(0);
      setLeft(false);
      setRight(true);
    }
  }, []);

  const scrollToLeft = () => {
    const scrollAmount = 200; // Adjust the scroll distance as needed
    if (containerRef.current != null) {
      setScrolled(scrolled+1)
      // containerRef.current.scrollLeft -= scrollAmount;
      setScroll(containerRef.current.scrollLeft - 200)
  }
  };

  const scrollToRight = () => {
    if (containerRef.current) {
      setScrolled(scrolled+1)

      const scrollAmount = 200; // Adjust the scroll distance as needed
      // containerRef.current.scrollLeft += scrollAmount;
      setScroll(containerRef.current.scrollLeft + 200)
    }
  };

  return (
    <div>
      <Link to ={"/createProject"}>Create  A Project</Link>
    {clicked && <div className="page-white" onClick={(e)=>{setClicked(false)}}></div>}
      <div>View Popular Projects By Language!</div>
    <div className="whole-page">
    <div className="home-page" ref={containerRef}>
      {pageWidth < 2992 && right ? <div><div className="svg-background-right"></div><img className="scroll-right" onClick={scrollToRight} src="data:image/png;base64,"/> </div>: ""}
      {pageWidth < 2992 && left ? <div><div className="svg-background-left"></div><img className="scroll-left" onClick={scrollToLeft} src="data:image/png;base64,"/> </div>: ""}
      <CategoryTab text={'Biology'} img={<img src="data:image/png;base64,"></img>}/>
<CategoryTab text={'Engineering'} img={<img src="data:image/png;base64,"></img>}/>
        <CategoryTab text={'Chemistry'} img={<img src="data:image/png;base64,"></img>}/>
        <CategoryTab text={'Physics'} img={<img src="data:image/png;base64,"></img>}/>
        <CategoryTab text={'Astronomy'}  img={<img src="data:image/png;base64,"></img>}/>
        <CategoryTab text={'Geology'} img={<img src="data:image/png;base64,"></img>}/>
    <CategoryTab text={'Meteorology'} img={<img src="data:image/png;base64,"></img>}/>
    <CategoryTab text={'Neurology'} img={<img src="data:image/png;base64,"></img>}/>
    <CategoryTab text={'Medicine'} img={<img src="data:image/png;base64,"></img>}/>
<CategoryTab text={'AI'} img={<img src="data:image/png;base64,"></img>}/>

</div>
    <div className="search-container">
      <div className="img-container">
        
        <img className='home-search-img' src="https://www.getfoundquick.com/wp-content/uploads/2017/10/shutterstock_588813473.jpg"></img>
        <form>
          <input placeholder="Search For Projects" className="search-bar" style={{borderRadius: clicked ? '10px 10px 0px 0px' : '', top: clicked ? '30%' : ''}} onClick={()=>{setClicked(true)}}></input>
          
         {clicked &&
            <div className='search-results'></div>
         } 
        </form>

      </div>
      </div>
      </div>
      </div>
  )
}

export default WelcomeComponent;